import { Component, EventEmitter } from '@angular/core';
import { Emitters } from '../emitters/emitters';
import { HttpClient } from '@angular/common/http';
import { CommonModule } from '@angular/common';
import { BrowserModule } from '@angular/platform-browser';


@Component({
  selector: 'app-nav-bar',
  templateUrl: './nav-bar.component.html',
  styleUrls: ['./nav-bar.component.css']
})
export class NavBarComponent {
authenticated=false;
constructor(private http:HttpClient){
}
ngOnInit():void{
Emitters.authEmitter.subscribe(
  (auth:boolean)=>{

  }
);

}
logout():void{
this.http.post('http://localhost:8000/users/logut',{},{withCredentials:true}).subscribe(()=>{
  this.authenticated=false
})
}
}
<header class="d-flex flex-wrap align-items-center justify-content-center justify-content-md-between py-3 mb-4 border-bottom">
  <div class="col-md-3 mb-2 mb-md-0">
    <a href="/" class="d-inline-flex link-body-emphasis text-decoration-none">
      <svg class="bi" width="40" height="32" role="img" aria-label="Bootstrap"><use xlink:href="#bootstrap"></use></svg>
    </a>
  </div>

  <ul class="nav col-12 col-md-auto mb-2 justify-content-center mb-md-0">
    <li><a href="#" class="nav-link px-2">Home</a></li>
    <li><a href="#" class="nav-link px-2">Accommodations</a></li>
  </ul>

  <div class="col-md-3 text-end" *ngif="!authenticated">
   <a routerLink="login"> <button type="button" class="btn btn-outline-primary me-2">Login</button></a>
   <a routerLink="sign-up"><button type="button" class="btn btn-primary">Sign-up</button></a>
  </div>

  <div class="col-md-3 text-end" *ngif="authenticated">
    <a routerLink=""> <button type="button" class="btn btn-outline-primary me-2" (click)="logout()">Logout</button></a>
   </div>
</header>
