import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';

interface Accommodation {
  id: string;
  name: string;
  location: string;
  amenities: string[];
  minGuests: number;
  maxGuests: number;
  images: string[];
  availability: string;
  pricePerNight: number;
}

@Component({
  selector: 'app-accommodations',
  templateUrl: './accommodations.component.html',
  styleUrls: ['./accommodations.component.css']
})
export class AccommodationsComponent implements OnInit {
  accommodations: Accommodation[] = [];

  constructor(private http: HttpClient) {}

  ngOnInit(): void {
    this.fetchAccommodations();
  }

  fetchAccommodations(): void {
    this.http.get<Accommodation[]>('http://localhost:8001/accommodations')
      .subscribe(
        (data: Accommodation[]) => {
          this.accommodations = data;
        },
        (error) => {
          console.error(error);
        }
      );
  }
}
<!DOCTYPE html>
<html>

<head>
  <meta charset="utf-8">
  <title>Accommodations</title>
  <base href="/">
  <meta name="viewport" content="width=device-width, initial-scale=1">
  <link href="https://cdn.jsdelivr.net/npm/bootstrap@5.3.2/dist/css/bootstrap.min.css" rel="stylesheet"
    integrity="sha384-T3c6CoIi6uLrA9TneNEoa7RxnatzjcDSCmG1MXxSR1GAsXEV/Dwwykc2MPK8M2HN" crossorigin="anonymous">
</head>

<body>
  <div class="container">
    <div class="row row-cols-1 row-cols-md-3 g-4">
      <div class="col" *ngFor="let accommodation of accommodations">
        <div class="card shadow-sm">
          <img [src]="accommodation.images[0]" class="bd-placeholder-img card-img-top" width="100%" height="225"
            alt="Accommodation Image">
          <div class="card-body">
            <h5 class="card-title">{{ accommodation.name }}</h5>
            <p class="card-text">Location: {{ accommodation.location }}</p>
            <p class="card-text">Guests: {{ accommodation.minGuests }} - {{ accommodation.maxGuests }}</p>
            <div class="d-flex justify-content-between align-items-center">
              <div class="btn-group">
                <button type="button" class="btn btn-sm btn-outline-secondary">View</button>
                <button type="button" class="btn btn-sm btn-outline-secondary">Edit</button>
              </div>
              <small class="text-body-secondary">0 Stars</small>
              <small class="text-body-secondary">Price/Night: ${{ accommodation.pricePerNight }}</small>
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>
</body>

</html>
